package uk.gov.legislation.data.virtuoso.queries;import org.springframework.stereotype.Component;import uk.gov.legislation.data.virtuoso.Virtuoso;import uk.gov.legislation.data.virtuoso.model.Resources;import java.io.IOException;@Componentpublic class SparqlQueries {private final Virtuoso virtuoso;    public SparqlQueries(Virtuoso virtuoso) {        this.virtuoso = virtuoso;    }    /**     * Query for getting Class Information     */    public String getClassData(String type, String format) throws IOException, InterruptedException {        String query = """                SELECT ?s ?p ?o                WHERE {                   BIND(<%s%s> AS ?s)                   ?s ?p ?o                }            """.formatted(Resources.Leg.Prefix, type);        return virtuoso.query(query, format);    }}