package uk.gov.legislation.endpoints.classinfo.api;import io.swagger.v3.oas.annotations.Parameter;import io.swagger.v3.oas.annotations.tags.Tag;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import java.io.IOException;import java.util.List;import java.util.Map;@Tag(name = "Class Info", description = "APIs for fetching class information")public interface ClassInfoApi {    @GetMapping(value = "/def/legislation/{className}", produces = MediaType.APPLICATION_JSON_VALUE)    ResponseEntity<Map<String, List <Object>>> getClassInfo(            @PathVariable @Parameter(description = "Type of class", example = "Item")            String className    ) throws IOException, InterruptedException;}