package uk.gov.legislation.endpoints.classinfo.controller;import jakarta.servlet.http.HttpServletRequest;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.RestController;import uk.gov.legislation.endpoints.classinfo.api.ClassInfoApi;import uk.gov.legislation.endpoints.classinfo.service.ClassInfoService;import java.io.IOException;@RestControllerpublic class ClassInfoApiController implements ClassInfoApi {    private final ClassInfoService service;    public ClassInfoApiController(ClassInfoService service) {        this.service = service;    }    @Override    public ResponseEntity <?> getClassInfo(String className, HttpServletRequest acceptHeader) throws IOException, InterruptedException {         String results = service.retrieveClassDataAsJson(className, acceptHeader);        return ResponseEntity.ok(results);    }}