package uk.gov.legislation.endpoints.classinfo.controller;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.RestController;import uk.gov.legislation.endpoints.classinfo.api.ClassInfoApi;import uk.gov.legislation.endpoints.classinfo.service.ClassInfoService;import java.io.IOException;import java.util.List;import java.util.Map;@RestControllerpublic class ClassInfoApiController implements ClassInfoApi {    private final ClassInfoService service;    public ClassInfoApiController(ClassInfoService service) {        this.service = service;    }    @Override    public ResponseEntity <Map<String, List<Object>>> getClassInfo(String className) throws IOException, InterruptedException {        Map<String, List<Object>> results = service.retrieveClassDataAsJson(className);        return results.isEmpty() ? ResponseEntity.notFound().build() : ResponseEntity.ok(results);    }}