package uk.gov.legislation.endpoints.classinfo.controller;import jakarta.servlet.http.HttpServletRequest;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.RestController;import uk.gov.legislation.data.virtuoso.queries.SparqlQueries;import uk.gov.legislation.endpoints.classinfo.api.ClassInfoApi;import java.io.IOException;@RestControllerpublic class ClassInfoApiController implements ClassInfoApi {    private final SparqlQueries queries;    public ClassInfoApiController(SparqlQueries queries) {        this.queries = queries;    }    @Override    public ResponseEntity <?> getClassInfo(String className, HttpServletRequest acceptHeader) throws IOException, InterruptedException {         String results = queries.getClassData(className, acceptHeader);        return ResponseEntity.ok(results);    }}