package uk.gov.legislation.endpoints.ld.sparql.api;import io.swagger.v3.oas.annotations.Parameter;import io.swagger.v3.oas.annotations.media.Schema;import jakarta.servlet.http.HttpServletRequest;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import java.io.IOException;@RequestMapping(value = "/ld/sparql")public interface SparqlApi {    @RequestMapping(method = {RequestMethod.GET, RequestMethod.POST}, consumes = "application/x-www-form-urlencoded")    ResponseEntity<String> sparql(            @RequestParam            @Parameter(                    description = "SPARQL query",                    example = """                            PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>                            PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>                            PREFIX leg: <http://www.legislation.gov.uk/def/legislation/>                            SELECT * WHERE {                               ?s ?p ?o                            }                            LIMIT 10                            """            )            String query,            @RequestParam(defaultValue = "json")            @Parameter(                    description = "Response format",                    schema = @Schema(allowableValues = {"json", "xml", "csv", "text"})            ) String format,            HttpServletRequest request    ) throws IOException, InterruptedException;}